(()=>{document.addEventListener("DOMContentLoaded",()=>{const e=postContent.querySelectorAll("p");e.forEach(e=>{const t=e.querySelector("img");t&&t.id!=="emoji"&&!t.classList.contains("emoji")&&(e.style.textAlign="center",e.style.fontSize="90%")});const t=postContent.querySelectorAll("img");t.forEach(e=>{if(e.classList.contains("emoji"))return;if(e.closest("p"))return;if(e.id==="emoji")return;if(!e.alt)return;const t=e.nextSibling;if(t&&t.nodeType===Node.TEXT_NODE&&t.textContent.trim()){const n=document.createElement("span");n.textContent=t.textContent.trim(),n.style.display="block",n.style.textAlign="center",n.style.fontSize="90%",e.parentNode.replaceChild(n,t)}})})})()